import edit from './edit.json';
import solution_1 from './solution_1.json';
import solution_2 from './solution_2.json';
import solution_3 from './solution_3.json';
import solution_4 from './solution_4.json';

// Add ids to the feature. It was difficult to get the new values generated by generateId. 
const addFeatureIds = (data) => {
    const featuresWithIds = data.features.map( (value, index) => {value.id = index; return value} );
    data.features = featuresWithIds;
    return data;
}

// Mock a data provider.
const solutions = [
    {name: 'solution 1', id: '1', reference: addFeatureIds(solution_1), edit},
    {name: 'solution 2', id: '2', reference: addFeatureIds(solution_2), edit},
    {name: 'solution 3', id: '3', reference: addFeatureIds(solution_3), edit},
    {name: 'solution 4', id: '4', reference: addFeatureIds(solution_4), edit}
];

export default solutions;